import {
  Navbar,
  Nav, Button, NavItem, ButtonGroup, Container, Row, Col, Carousel } from 'react-bootstrap';
  import { Spring } from  'react-spring/renderprops';
  import Slider from "react-slick";
import sliderImg1 from './backgroundimg/01.jpg';
import sliderImg2 from './backgroundimg/02.jpg';
import sliderImg3 from './backgroundimg/03.jpg';
import sliderImg4 from './backgroundimg/04.jpg';
import sliderImg5 from './backgroundimg/05.jpg';
import sliderImg6 from './backgroundimg/06.jpg';
import gif from './backgroundimg/giphy.gif';
import logo from './img/sch.png';
import BackgroundSlideshow from 'react-background-slideshow'

// Add CDN to HTML For Slider
//<link rel="stylesheet" type="text/css" charset="UTF-8" href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick.min.css" />
/* <link rel="stylesheet" type="text/css" href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick-theme.min.css" /> */
class App extends Component {

  render() {
    //js
    var settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1
    };










  // Styles


  // const body = {
  //   backgroundImage: `url(${sliderImg1})`,
  //   backgroundPosition: 'center',
  //   backgroundRepeat: 'no-repeat',
  //   backgroundSize: 'cover'
  // }

const imgDem = {
  width:'100%',
  height:'100%',
  backgroundSize:'cover',
  position:'relative'
}
const buttonStyle = {
  textAlign:'center',



}

const logoStyle = {
  width:'300px',
  height:'auto',


}

const ride = {
  textAlign:'center',
  fontSize:'70px',
  color:'white'

}
const h1Edit = {
  fontSize:'70px',
  position:'relative',
  color:'black',
  marginRight:'50px'

}

const align = {
  textAlign:'center',
  position: 'relative'
}

const slideShow = {
  position: 'absolute',
  width:'100%',
  height:'auto'
}
const navBar ={}
    return (
<div>

<Spring
        from={{ opacity: 0.9}}
        to={{opacity: 1}}
        config={{ delay: 1000, duration:100000000000000000000000000000 }}
        >
        { props => (
          <div style={props}>
        {/* Insert below */}
        <div>
        <BackgroundSlideshow style={slideShow} images={[ sliderImg1, sliderImg2, sliderImg3, sliderImg4, sliderImg5 ]} />
          </div>

          </div>
        ) }
          </Spring>






<div style={navBar}>
<Spring
from={{ opacity: 0, marginTop:-500}}
to={{opacity: 1, marginTop:0 }}
config={{ delay: 200, duration:1000 }}

>

{ props => (
  <div style={props}>
{/* Insert below */}

<Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
<Navbar.Brand href="#home">
      <img
        src={logo}
        width="90"
        height="auto"
        className="d-inline-block align-top"
        alt="Bike logo"
      />
    </Navbar.Brand>
  <Navbar.Toggle aria-controls="responsive-navbar-nav" />
  <Navbar.Collapse id="responsive-navbar-nav">
    <Nav className="mr-auto">
      <Nav.Link href="#"></Nav.Link>
      <Nav.Link href="#"></Nav.Link>

    </Nav>
    <Nav>
      <Nav.Link href="#">Main</Nav.Link>
      <Nav.Link eventKey={2} href="#">
        Khanban
      </Nav.Link>
      <Nav.Link href="#">Product</Nav.Link>
      <Nav.Link eventKey={2} href="#">
        Pricing
      </Nav.Link>
      <Nav.Link href="#">Login</Nav.Link>
      <Nav.Item>
      <ButtonGroup className="mr-2" aria-label="First group">
    <Button>Sign up</Button>
  </ButtonGroup>
  </Nav.Item>
    </Nav>
  </Navbar.Collapse>
</Navbar>
  </div>
) }
</Spring>
</div>




          <Slider {...settings}>
        <div>
          {/* Slider 1 below */}
          <Spring
        from={{ opacity: 0}}
        to={{opacity: 1 }}
        config={{ delay: 1000, duration:1000 }}
        >
        { props => (
          <div style={props}>
        {/* Insert below */}
        <div>
          <Container>
            <Row>
              <Col></Col>
              <h1 style={h1Edit}>Welcome</h1>
              <Col></Col>
            </Row>
          </Container>

          </div>

          </div>
        ) }
          </Spring>

          <Spring
        from={{ opacity: 0}}
        to={{opacity: 1}}
        config={{ delay: 600, duration:1000 }}
        >
        { props => (
          <div style={props}>
        {/* Insert below */}
        <Container>
          <Row>
            <Col></Col>
            <Col>
            <div style={align}>
        <img src={logo} style={logoStyle} />
          </div>
            </Col>
            <Col></Col>
          </Row>
        </Container>


          </div>
        ) }
          </Spring>

          <Spring
        from={{ opacity: 0, marginRight:-500}}
        to={{opacity: 1, marginRight:0 }}
        config={{ delay: 1000, duration:1000 }}
        >
        { props => (
          <div style={props}>
        {/* Insert below */}
        <div style={align}>

            <h1 style={ride}>Riding For Research</h1>


          </div>

          </div>
        ) }
          </Spring>

          <Spring
        from={{ opacity: 0.9}}
        to={{opacity: 1}}
        config={{ delay: 1100, duration:100000000000000000000000000000000 }}
        >
        { props => (
          <div style={props}>
        {/* Insert below */}
       <div style={buttonStyle}>
          <Button variant="primary">Learn More</Button>
       </div>
          </div>
        ) }
          </Spring>

        </div>
        <div>
          {/* Slider 2 below */}

        </div>
        <div>
          {/* Slider 3 below */}

        </div>
        <div>
          {/* Slider 4 below */}

        </div>
        <div>
          {/* Slider 5 below */}

        </div>
        <div>
          {/* Slider 6 below */}

        </div>
      </Slider>






















        </div>
    )
  }
}


export default App
