// In order to get the landing page to move/look like its suppose to, please insert the following links in the index.html head tag
// Bootstrap ------>
// <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css" integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T" crossorigin="anonymous"/>
// Google Fonts optional -------->
// <link href="https://fonts.googleapis.com/css?family=Gochi+Hand|Patrick+Hand+SC" rel="stylesheet">




import React, { Component } from 'react';
import {
  Navbar,
  Nav, Button, ButtonGroup, Container, Row, Col, } from 'react-bootstrap';
  import { Spring } from  'react-spring/renderprops';
  import welcome from './components/component1';
import sliderImg1 from './backgroundimg/01.jpg';
import sliderImg2 from './backgroundimg/02.jpg';
import sliderImg3 from './backgroundimg/03.jpg';
import sliderImg4 from './backgroundimg/04.jpg';
import sliderImg5 from './backgroundimg/05.jpg';
import logo from './img/sch.png';
import BackgroundSlideshow from 'react-background-slideshow';
import ReactTextRotator from 'react-text-rotator';
import makeCarousel from 'react-reveal/makeCarousel';
// we'll need the Slide component for sliding animations
// but you can use any other effect
import Slide from 'react-reveal/Slide';
// we'll use styled components for this tutorial
// but you can use any other styling options ( like plain old css )
import styled, { css } from 'styled-components';


class App extends Component {

  render() {
    const width = '500px', height='500px';
const Container = styled.div`
  border: 1px solid red;
  position: relative;
  overflow: hidden;
  width: ${width};
`;
const Children  = styled.div`
  width: ${width};
  position: relative;
  height: ${height};
`;
const Arrow = styled.div`
  text-shadow: 1px 1px 1px #fff;
  z-index: 100;
  line-height: ${height};
  text-align: center;
  position: absolute;
  top: 0;
  width: 10%;
  font-size: 3em;
  cursor: pointer;
  user-select: none;
  ${props => props.right ? css`left: 90%;` : css`left: 0%;`}
`;
const Dot = styled.span`
  font-size: 1.5em;
  cursor: pointer;
  text-shadow: 1px 1px 1px #fff;
  user-select: none;
`;
const Dots = styled.span`
  text-align: center;
  width: ${width};
  z-index: 100;
`;
const CarouselUI = ({ position, total, handleClick, children }) => (
  <Container>
    <Children>
      {children}
      <Arrow onClick={handleClick} data-position={position - 1}>{'<'}</Arrow>
      <Arrow right onClick={handleClick} data-position={position + 1}>{'>'}</Arrow>
    </Children>
    <Dots>
      {Array(...Array(total)).map( (val, index) =>
        <Dot key={index} onClick={handleClick} data-position={index}>
          {index === position ? '● ' : '○ ' }
        </Dot>
      )}
    </Dots>
  </Container>
);
const Carousel = makeCarousel(CarouselUI);
    //js objects
    const headers = [
      {
        text:'Welcome',
        className:'firstHeader',
        animation:'fade'
      },
      {
        text:'NYC to Alaska',
        className:'secHeader',
        animation:'fade'
      },
      {
        text:'Follow Along',
        className:'thirdHeader',
        animation:'fade'
      },
      {
        text:'Health Analysis',
        className:'fourthHeader',
        animation:'fade'
      },
      {
        text:'Meal Tracking',
        className:'fifthHeader',
        animation:'fade'
      },
      {
        text:'Share The Experience',
        className:'firstHeader',
        animation:'fade'
      },
    ]

    const content = [
      {
        text: 'Riding For Research',
        className: 'classA',
        animation: 'fade',
      },
      {
        text: 'Tune in to watch interactive live streams as the joureny unfolds to learn what each day has in store and for highlights along the way.',
        className: 'classB',
        animation: 'fade',
      },
      {
        text: 'Utilize an interactive map to learn about the daily weather conditions and track his progress as he endures the elements.',
        className: 'classC',
        animation: 'fade',
      },
      {
        text: 'Understand how various data sources drive the research and explore relationships using real-time data visualizations.',
        className: 'classD',
        animation: 'fade',
      },
      {
        text: 'With a minimal supply, discover the foods needed to keep the wheels spinning over 6 hours each day.',
        className: 'classE',
        animation: 'fade',
      },
      {
        text: 'Enjoy the ride coast to coast. See what he sees and view highlights from the best spots throughout.',
        className: 'classE',
        animation: 'fade',

      }
    ];


  //  js Styles

const slideShow = {
  position: 'absolute',
  width:'100%',
  height:'auto'
}
const logoStyle = {
  width:'200px',
  height:'auto',
}
const align = {
  textAlign:'center',
  position: 'relative'
}
const navBar ={}

const h1Edit = {
  fontSize:'55px',
  position:'relative',
  color:'#cf7907',
  marginRight:'20px',
  textAlign:'center',
  fontFamily:'Gochi Hand, cursive'
}
const infoSlide = {
  fontSize: '17px',
  textAlign:'center',
  color:'black',
  fontFamily:'Patrick Hand SC, cursive'
}
const buttonz = {
  textAlign:'center',
  marginTop:'15px',
  fontFamily:'Patrick Hand SC, cursive',
}
const navSty = {
  fontFamily:'Gochi Hand, cursive'
}
    return (
<div>
{/* Background */}
<Spring
        from={{ opacity: 0.8}}
        to={{opacity: 1}}
        config={{ delay: 100, duration:100000000000000000000000000000 }}
        >
        { props => (
          <div style={props}>
        {/* Insert below */}
        <div>
        <BackgroundSlideshow style={slideShow} images={[ sliderImg1, sliderImg2, sliderImg3, sliderImg4, sliderImg5 ]} />
          </div>

          </div>
        ) }
          </Spring>


          {/* Navbar */}

        <div style={navBar}>
          <Spring
            from={{ opacity: 0, marginTop:-500}}
            to={{opacity: 1, marginTop:0 }}
            config={{ delay: 200, duration:1000 }}
            >
            { props => (
              <div style={props}>
                {/* Insert below */}
                <div style={navSty}>
                  <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
                  <Navbar.Brand href="#home">
                        <img
                          src={logo}
                          width="90"
                          height="auto"
                          className="d-inline-block align-top"
                          alt="Bike logo"
                        />
                      </Navbar.Brand>
                    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                    <Navbar.Collapse id="responsive-navbar-nav">
                      <Nav className="mr-auto">
                        <Nav.Link href="#"></Nav.Link>
                        <Nav.Link href="#"></Nav.Link>

                      </Nav>
                      <Nav>
                        <Nav.Link href="#">About</Nav.Link>
                        <Nav.Link eventKey={2} href="#">
                          Live
                        </Nav.Link>
                        <Nav.Link href="#">Media</Nav.Link>
                        <Nav.Link eventKey={2} href="#">
                          Location
                        </Nav.Link>
                        <Nav.Link href="#">Fitness</Nav.Link>
                        <Nav.Link href="#">Diet</Nav.Link>
                        <Nav.Item>
                        <ButtonGroup className="mr-2" aria-label="First group">
                        <Button variant="light">Donate</Button>
                    </ButtonGroup>
                    </Nav.Item>
                      </Nav>
                    </Navbar.Collapse>
                  </Navbar>
                    </div>
                    </div>
                  ) }
                  </Spring>
                  </div>
<Container>
  <Row>
    <Col>
    <Carousel>
    <Slide right>
      <div>
      <Spring
        from={{ opacity: 0, marginLeft:-500}}
        to={{opacity: 1, marginLeft:0}}
        config={{ delay: 2000, duration:1000 }}
        >
        { props => (
          <div style={props}>
        {/* Insert below */}
        <Container>
          <Row>
            <Col></Col>
              <div style={h1Edit}>
            <Col><ReactTextRotator 
                  content={headers}
                  time={11000}
                  startDelay={0}
            /></Col>
              </div>
            <Col></Col>
          </Row>
        </Container>

          </div>
        ) }
          </Spring>
          <Spring
        from={{ opacity: 0}}
        to={{opacity: 1}}
        config={{ delay: 2500, duration:1000 }}
        >
        { props => (
          <div style={props}>
        {/* Insert below */}
        <Container>
          <Row>
            <Col></Col>
            <Col>
            <div style={align}>
        <img src={logo} style={logoStyle} />
          </div>
            </Col>
            <Col></Col>
          </Row>
        </Container>
          </div>
        ) }
          </Spring>


          <Spring
        from={{ opacity: 0, marginRight:-500}}
        to={{opacity: 1, marginRight:0}}
        config={{ delay: 3000, duration:1000 }}
        >
        { props => (
          <div style={props}>
        {/* Insert below */}
        <Container>
          <Row>
            <Col></Col>
            <Col><div style={infoSlide}>
              <ReactTextRotator
              content={content}
              time={11000}
              startDelay={0}
              />
          </div>
          </Col>
          <Col></Col>
        </Row>
    </Container>
          </div>
        ) }
          </Spring>



          <Spring
        from={{ opacity: 0, marginRight:-500}}
        to={{opacity: 1, marginRight:0}}
        config={{ delay: 3000, duration:1000 }}
        >
        { props => (
          <div style={props}>
        {/* Insert below */}
        <Container>
          <Row>
            <Col></Col>
            <Col><div style={h1Edit}>
              <ReactTextRotator
              content={welcome}
              time={11000}
              startDelay={0}
              />
          </div>
          </Col>
          <Col></Col>
        </Row>
    </Container>
          </div>
        ) }
          </Spring>
{/* Buttons */}
<Spring
        from={{ opacity: 0, marginBottom:-500}}
        to={{opacity: 1, marginBottom:0}}
        config={{ delay: 4000, duration:1000 }}
        >
        { props => (
          <div style={props}>
        {/* Insert below */}
        <Container>
          <Row>
            <Col></Col>
              <div style={buttonz}>
            <Col><Button variant="dark">Learn More</Button></Col>
              </div>
            <Col></Col>
          </Row>
        </Container>

          </div>
        ) }
          </Spring>
      </div>
    </Slide>
    <Slide right>
      <div>
        <h1>Slide 2</h1>
        <p>Slide Description</p>
      </div>
    </Slide>
    <Slide right>
      <div>
        <h1>Slide 3</h1>
        <p>Slide Description</p>
      </div>
    </Slide>
  </Carousel>
    </Col>
  </Row>
</Container>











        </div>
    )
  }
}


export default App
