import React, { Component } from 'react';
import {
  Navbar,
  Nav, Button, NavItem, ButtonGroup, Container, Row, Col } from 'react-bootstrap';
  import { Spring } from  'react-spring/renderprops';
  import Slider from "react-slick";
import sliderImg1 from './backgroundimg/01.jpg';
import sliderImg2 from './backgroundimg/02.jpg';
import sliderImg3 from './backgroundimg/03.jpg';
import sliderImg4 from './backgroundimg/04.jpg';
import sliderImg5 from './backgroundimg/05.jpg';
import sliderImg6 from './backgroundimg/06.jpg';
import gif from './backgroundimg/giphy.gif';
import logo from './img/sch.png';
import BackgroundSlideshow from 'react-background-slideshow'
// Add CDN to HTML For Slider
//<link rel="stylesheet" type="text/css" charset="UTF-8" href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick.min.css" />
/* <link rel="stylesheet" type="text/css" href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick-theme.min.css" /> */
class App extends Component {

  render() {
    //js
    var settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1
    };










  // Styles


  // const body = {
  //   backgroundImage: `url(${sliderImg1})`,
  //   backgroundPosition: 'center',
  //   backgroundRepeat: 'no-repeat',
  //   backgroundSize: 'cover'
  // }

const imgDem = {
  width:'100%',
  height:'100%',
  backgroundSize:'cover',
  position:'relative'
}
const buttonStyle = {
  textAlign:'center',



}

const logoStyle = {
  width:'300px',
  height:'auto',


}

const ride = {
  textAlign:'center',
  fontSize:'70px',
  color:'white'

}
const h1Edit = {
  fontSize:'70px',
  textAlign:'center',
  position:'relative',
  color:'black'

}
const nineHone = {
  fontSize:'40px',
  textAlign:'center',
  position:'relative',
  bottom:'400px',
}
const align = {
  textAlign:'center',
  position: 'relative'
}
const body1 ={
  backgroundImage: `url(${sliderImg1})`,

  width:'1080px',
  height:'1080px'
}
const body2 ={
  backgroundImage: `url(${sliderImg2})`
}
const body3 ={
  backgroundImage: `url(${sliderImg3})`
}
const body4 ={
  backgroundImage: `url(${sliderImg4})`
}
const body5 ={
  backgroundImage: `url(${sliderImg5})`
}
const body6 ={
  backgroundImage: `url(${sliderImg6})`
}
const slideShow = {
  position: 'relative',
  top: '0',
  left: '0'
}
const navBar ={
  position: 'relative',
  top: '0',
  left: '0'
}
const child ={
  position: 'relative',
  top: '30px',
  left: '70px'
}
    return (
<div>
  <div style={slideShow}>
  <BackgroundSlideshow style={slideShow} images={[ sliderImg1, sliderImg2, sliderImg3 ]} />

<div style={navBar}>
<Spring
from={{ opacity: 0, marginTop:-50}}
to={{opacity: 1, marginTop:0 }}
config={{ delay: 100, duration:1000 }}

>

{ props => (
  <div style={props}>
{/* Insert below */}

<Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
<Navbar.Brand href="#home">
      <img
        src={logo}
        width="90"
        height="auto"
        className="d-inline-block align-top"
        alt="Bike logo"
      />
    </Navbar.Brand>
  <Navbar.Toggle aria-controls="responsive-navbar-nav" />
  <Navbar.Collapse id="responsive-navbar-nav">
    <Nav className="mr-auto">
      <Nav.Link href="#"></Nav.Link>
      <Nav.Link href="#"></Nav.Link>

    </Nav>
    <Nav>
      <Nav.Link href="#">Main</Nav.Link>
      <Nav.Link eventKey={2} href="#">
        Khanban
      </Nav.Link>
      <Nav.Link href="#">Product</Nav.Link>
      <Nav.Link eventKey={2} href="#">
        Pricing
      </Nav.Link>
      <Nav.Link href="#">Login</Nav.Link>
      <Nav.Item>
      <ButtonGroup className="mr-2" aria-label="First group">
    <Button>Sign up</Button>
  </ButtonGroup>
  </Nav.Item>
    </Nav>
  </Navbar.Collapse>
</Navbar>
  </div>
) }
</Spring>
</div>

  </div>










    <div style={child}>

     <Spring
        from={{ opacity: 0}}
        to={{opacity: 1 }}
        config={{ delay: 1000, duration:1000 }}
        >
        { props => (
          <div style={props}>
        {/* Insert below */}
        <div>
        <h1 style={h1Edit}>Welcome</h1>
          </div>

          </div>
        ) }
          </Spring>

          <Spring
        from={{ opacity: 0, marginTop:-500}}
        to={{opacity: 1 , marginTop:20}}
        config={{ delay: 400, duration:1000 }}
        >
        { props => (
          <div style={props}>
        {/* Insert below */}
        <div style={align}>
        <img src={logo} style={logoStyle} />
          </div>

          </div>
        ) }
          </Spring>

          <Spring
        from={{ opacity: 0, marginRight:-500}}
        to={{opacity: 1, marginRight:0 }}
        config={{ delay: 1000, duration:1000 }}
        >
        { props => (
          <div style={props}>
        {/* Insert below */}
        <div style={align}>
             <h1 style={ride}>Riding For Research</h1>
          </div>

          </div>
        ) }
          </Spring>

          <Spring
        from={{ opacity: 0}}
        to={{opacity: 1}}
        config={{ delay: 1100, duration:1000 }}
        >
        { props => (
          <div style={props}>
        {/* Insert below */}
       <div style={buttonStyle}>
          <Button variant="primary">Learn More</Button>
       </div>
          </div>
        ) }
          </Spring>





{/* 2nd Slide/IMG start */}
    </div>










<br/>
<br/>
<br/>
<br/>


        </div>





















    )
  }
}


export default App
