import React, { Component } from 'react';
import {
  Navbar,
  Nav, Button, NavItem, ButtonGroup, Container, Row, Col, Carousel } from 'react-bootstrap';
  import { Spring } from  'react-spring/renderprops';
  import Slider from "react-slick";
  import welcome from './components/component1';
  import nyc from './components/component2';
import sliderImg1 from './backgroundimg/01.jpg';
import sliderImg2 from './backgroundimg/02.jpg';
import sliderImg3 from './backgroundimg/03.jpg';
import sliderImg4 from './backgroundimg/04.jpg';
import sliderImg5 from './backgroundimg/05.jpg';
import sliderImg6 from './backgroundimg/06.jpg';
import gif from './backgroundimg/giphy.gif';
import logo from './img/sch.png';
import BackgroundSlideshow from 'react-background-slideshow';
import ReactTextRotator from 'react-text-rotator';

// Add CDN to HTML For Slider
//<link rel="stylesheet" type="text/css" charset="UTF-8" href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick.min.css" />
/* <link rel="stylesheet" type="text/css" href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick-theme.min.css" /> */
class App extends Component {

  render() {
    //js
    var settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1
    };
    const headers = [
      {
        text:'Welcome To',
        className:'firstHeader',
        animation:'fade'
      },
      {
        text:'NYC to Alaska',
        className:'secHeader',
        animation:'fade'
      },
      {
        text:'Follow Along',
        className:'thirdHeader',
        animation:'fade'
      },
      {
        text:'Health Analysis',
        className:'fourthHeader',
        animation:'fade'
      },
      {
        text:'Meal Tracking',
        className:'fifthHeader',
        animation:'fade'
      },
      {
        text:'Share The Experience',
        className:'firstHeader',
        animation:'fade'
      },
    ]

    const content = [
      {
        text: 'Riding For Research',
        className: 'classA',
        animation: 'fade',
      },
      {
        text: 'Tune in to watch interactive live streams as the joureny unfolds to learn what each day has in store and for highlights along the way',
        className: 'classB',
        animation: 'fade',
      },
      {
        text: 'Utilize an interactive map to learn about the daily weather conditions and track his progress as he endures the elements',
        className: 'classC',
        animation: 'fade',
      },
      {
        text: 'We shall fight in the hills.',
        className: 'classD',
        animation: 'fade',
      },
      {
        text: 'We shall never surrender...',
        className: 'classE',
        animation: 'fade',
      },
      {
        text: 'We shall never surrender...',
        className: 'classE',
        animation: 'fade',
      }
    ];

  // Styles

const slideShow = {
  position: 'absolute',
  width:'100%',
  height:'auto'
}
const navBar ={}


    return (
<div>



{/* Background */}
<Spring
        from={{ opacity: 0.9}}
        to={{opacity: 1}}
        config={{ delay: 100, duration:100000000000000000000000000000 }}
        >
        { props => (
          <div style={props}>
        {/* Insert below */}
        <div>
        <BackgroundSlideshow style={slideShow} images={[ sliderImg1, sliderImg2, sliderImg3, sliderImg4, sliderImg5 ]} />
          </div>

          </div>
        ) }
          </Spring>
{/* Words on background */}









<div style={navBar}>
<Spring
from={{ opacity: 0, marginTop:-500}}
to={{opacity: 1, marginTop:0 }}
config={{ delay: 200, duration:1000 }}

>

{ props => (
  <div style={props}>
{/* Insert below */}

<Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
<Navbar.Brand href="#home">
      <img
        src={logo}
        width="90"
        height="auto"
        className="d-inline-block align-top"
        alt="Bike logo"
      />
    </Navbar.Brand>
  <Navbar.Toggle aria-controls="responsive-navbar-nav" />
  <Navbar.Collapse id="responsive-navbar-nav">
    <Nav className="mr-auto">
      <Nav.Link href="#"></Nav.Link>
      <Nav.Link href="#"></Nav.Link>

    </Nav>
    <Nav>
      <Nav.Link href="#">Main</Nav.Link>
      <Nav.Link eventKey={2} href="#">
        Khanban
      </Nav.Link>
      <Nav.Link href="#">Product</Nav.Link>
      <Nav.Link eventKey={2} href="#">
        Pricing
      </Nav.Link>
      <Nav.Link href="#">Login</Nav.Link>
      <Nav.Item>
      <ButtonGroup className="mr-2" aria-label="First group">
    <Button>Sign up</Button>
  </ButtonGroup>
  </Nav.Item>
    </Nav>
  </Navbar.Collapse>
</Navbar>
  </div>
) }
</Spring>
</div>




<Container>
  <Row>
    <Col></Col>
    <Col><ReactTextRotator
      content={headers}
      time={11000}
      startDelay={0}
    /></Col>
    <Col></Col>
  </Row>
</Container>
<Container>
  <Row>
    <Col></Col>
    <Col><ReactTextRotator
      content={content}
      time={11000}
      startDelay={0}
    /></Col>
    <Col></Col>
  </Row>
</Container>












        </div>
    )
  }
}


export default App
